CREATE TABLE IF NOT EXISTS `ims_eso_share_data` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL COMMENT '规则id', `from_user` varchar(50) NOT NULL DEFAULT '' COMMENT '用户openid', `userid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '用户uid', `eso_shareip` varchar(15) NOT NULL DEFAULT '' COMMENT '分享达人IP', `eso_sharetime` int(10) unsigned NOT NULL COMMENT '分享时间', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='分享达人' AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_eso_share_list` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL COMMENT '规则id', `from_user` varchar(50) NOT NULL DEFAULT '' COMMENT '用户openid', `userid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '用户uid', `name` varchar(50) NOT NULL DEFAULT '' COMMENT '姓名', `tel` varchar(50) NOT NULL DEFAULT '' COMMENT '电话', `eso_sharenum` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '分享量', `eso_sharetime` int(10) unsigned NOT NULL COMMENT '最后分享时间', `status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '是否禁止', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='分享达人' AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_eso_share_reply` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '规则id', `isname` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '是否需要绑定个人信息', `parentid` int(10) unsigned NOT NULL DEFAULT '0', `title` varchar(50) NOT NULL COMMENT '规则标题', `checkkeyword` varchar(50) NOT NULL COMMENT '查询关键词', `picture` varchar(100) NOT NULL COMMENT '图片', `start_time` int(10) unsigned NOT NULL COMMENT '开始时间', `end_time` int(10) unsigned NOT NULL COMMENT '结束时间', `description` varchar(255) NOT NULL COMMENT '描述', `content` text NOT NULL COMMENT '内容', `status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '开关状态', `r` int(10) unsigned NOT NULL DEFAULT '0', `z` int(10) unsigned NOT NULL DEFAULT '0', `u` varchar(255) NULL, `share_url` text NULL, `share_txt` text NULL, `share_desc` text NULL, `share_title` text NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 COMMENT='分享达人' AUTO_INCREMENT=1 ;

