CREATE TABLE IF NOT EXISTS `ims_lw_report` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL, `swnoId` int(10) unsigned NOT NULL, `openid` varchar(50) NOT NULL, `reporter` varchar(50) NOT NULL, `createtime` varchar(100) NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;

