manifest.install:
CREATE TABLE IF NOT EXISTS `ims_mbrp_activities` ( `actid` int(10) unsigned NOT NULL AUTO_INCREMENT COMMENT '活动编号', `uniacid` int(10) unsigned NOT NULL, `title` varchar(20) NOT NULL COMMENT '活动名称', `start` int(10) unsigned NOT NULL COMMENT '开始时间', `end` int(10) unsigned NOT NULL COMMENT '结束时间', `rules` text NOT NULL COMMENT '活动规则介绍', `guide` varchar(255) NOT NULL COMMENT '活动指南(图文素材地址)', `banner` varchar(500) NOT NULL COMMENT '背景图片', `type` varchar(10) NOT NULL COMMENT '活动类型(direct, shared)', `limit` varchar(1000) NOT NULL DEFAULT '', `share` varchar(1000) NOT NULL DEFAULT '', `tag` text NOT NULL, `amount` decimal(10,2) NOT NULL DEFAULT '0.00', PRIMARY KEY (`actid`), KEY `type` (`type`), KEY `uniacid` (`uniacid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_mbrp_activity_gifts` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `activity` int(10) unsigned NOT NULL, `gift` int(10) unsigned NOT NULL, `quantity` int(10) unsigned NOT NULL, `rate` decimal(6,2) NOT NULL COMMENT '中奖百分比率', PRIMARY KEY (`id`), KEY `activity` (`activity`), KEY `gift` (`gift`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_mbrp_fans` ( `uid` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) unsigned NOT NULL, `openid` varchar(40) NOT NULL DEFAULT '', `proxy` varchar(40) NOT NULL DEFAULT '', `unionid` varchar(40) NOT NULL DEFAULT '', `nickname` varchar(20) NOT NULL DEFAULT '', `gender` varchar(2) DEFAULT '', `state` varchar(20) NOT NULL DEFAULT '', `city` varchar(20) NOT NULL DEFAULT '', `country` varchar(20) NOT NULL DEFAULT '', `avatar` varchar(500) NOT NULL DEFAULT '', `status` tinyint(4) NOT NULL DEFAULT '0', PRIMARY KEY (`uid`), KEY `uniacid` (`uniacid`), KEY `openid` (`openid`), KEY `proxy` (`proxy`), KEY `nickname` (`nickname`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_mbrp_gifts` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL, `title` varchar(20) NOT NULL, `type` varchar(20) NOT NULL COMMENT 'cash - 现金红包, coupon - 券类', `tag` text NOT NULL, `remark` text NOT NULL, PRIMARY KEY (`id`), KEY `uniacid` (`uniacid`), KEY `type` (`type`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_mbrp_helps` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL, `activity` int(10) unsigned NOT NULL, `owner` int(10) unsigned NOT NULL, `helper` int(10) unsigned NOT NULL, `dateline` int(10) unsigned NOT NULL, PRIMARY KEY (`id`), KEY `activity` (`activity`), KEY `uniacid` (`uniacid`), KEY `owner` (`owner`), KEY `helper` (`helper`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_mbrp_profiles` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL, `uid` int(10) unsigned NOT NULL, `name` varchar(10) NOT NULL, `title` varchar(20) NOT NULL, `value` varchar(500) NOT NULL, PRIMARY KEY (`id`), KEY `name` (`name`), KEY `uniacid` (`uniacid`), KEY `uid` (`uid`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_mbrp_records` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL, `uid` int(10) unsigned NOT NULL, `activity` int(10) unsigned NOT NULL, `gift` int(10) unsigned NOT NULL, `fee` varchar(20) NOT NULL DEFAULT '', `log` varchar(500) NOT NULL DEFAULT '', `status` varchar(20) NOT NULL, `created` int(10) unsigned NOT NULL, `completed` int(10) unsigned NOT NULL DEFAULT '0', PRIMARY KEY (`id`), KEY `uid` (`uid`), KEY `activity` (`activity`), KEY `gift` (`gift`), KEY `log` (`log`(333)), KEY `uniacid` (`uniacid`), KEY `status` (`status`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_mbrp_trades` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL, `activity` int(10) unsigned NOT NULL, `uid` int(10) unsigned NOT NULL, `item` varchar(20) NOT NULL, `status` varchar(10) NOT NULL, `created` int(10) unsigned NOT NULL, `paid` int(10) unsigned NOT NULL, `completed` int(10) unsigned NOT NULL, PRIMARY KEY (`id`), KEY `uniacid` (`uniacid`), KEY `activity` (`activity`), KEY `uid` (`uid`), KEY `item` (`item`), KEY `status` (`status`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;

manifest.uninstall:
DROP TABLE IF EXISTS `ims_mbrp_fans`;
DROP TABLE IF EXISTS `ims_mbrp_records`;
DROP TABLE IF EXISTS `ims_mbrp_helps`;
