CREATE TABLE IF NOT EXISTS `ims_wdl_wifi_authentication` ( `id` int(11) NOT NULL AUTO_INCREMENT, `uniacid` int(11) DEFAULT NULL, `routerid` int(11) DEFAULT NULL, `fromuser` varchar(100) DEFAULT NULL, `createtime` int(11) DEFAULT NULL, `result` int(11) DEFAULT NULL, `resultmemo` varchar(255) DEFAULT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_wdl_wifi_moban` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `uniacid` int(10) unsigned NOT NULL, `type` varchar(10) NOT NULL, `style` varchar(10) NOT NULL, `title` varchar(200) NOT NULL, `shopname` varchar(200) NOT NULL, `shopaddress` varchar(200) NOT NULL, `shoptel` varchar(20) NOT NULL, `logo` varchar(100) NOT NULL, `qrcode` varchar(100) NOT NULL, `copyright` varchar(50) NOT NULL, `bgimg` varchar(2000) NOT NULL, `createtime` int(10) unsigned NOT NULL, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=1 ;
CREATE TABLE IF NOT EXISTS `ims_wdl_wifi_reply` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `rid` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '规则id', `oktip` varchar(255) NOT NULL COMMENT '规则标题', `routerid` int(10) unsigned NOT NULL COMMENT '节点id', `status` tinyint(3) unsigned NOT NULL DEFAULT '1' COMMENT '开关状态', PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8 AUTO_INCREMENT=7 ;
