CREATE TABLE IF NOT EXISTS `ims_weisrc_truth_question` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned default '0', `from_user` varchar(100) DEFAULT '', `title` varchar(200) NOT NULL DEFAULT '', `displayorder` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序', `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态', `dateline` int(10) DEFAULT 0, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_weisrc_truth_reply` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned default 0, `qid` int(10) unsigned default 0, `from_user` varchar(100) DEFAULT '', `parentid` int(10) unsigned default 0, `parentopenid` varchar(100) DEFAULT '' COMMENT '', `nickname` varchar(100) DEFAULT '', `headimgurl` varchar(500) DEFAULT '', `content` varchar(200) DEFAULT '' COMMENT '回复内容', `sharecount` int(10) unsigned default '0' COMMENT '分享次数', `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态', `displayorder` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序', `dateline` int(10) DEFAULT 0, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_weisrc_truth_share` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned default 0, `qid` int(10) unsigned default 0, `from_user` varchar(100) DEFAULT '', `parentid` int(10) unsigned default 0, `parentopenid` varchar(100) DEFAULT '' COMMENT '', `nickname` varchar(100) DEFAULT '', `headimgurl` varchar(500) DEFAULT '', `content` varchar(200) DEFAULT '' COMMENT '回复内容', `sharecount` int(10) unsigned default '0' COMMENT '分享次数', `status` tinyint(1) NOT NULL DEFAULT '1' COMMENT '状态', `displayorder` int(10) unsigned NOT NULL DEFAULT '0' COMMENT '排序', `dateline` int(10) DEFAULT 0, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
CREATE TABLE IF NOT EXISTS `ims_weisrc_truth_setting` ( `id` int(10) unsigned NOT NULL AUTO_INCREMENT, `weid` int(10) unsigned default 0, `title` varchar(100) DEFAULT '' COMMENT '网站名称', `share_title` varchar(100) NOT NULL DEFAULT '', `share_image` varchar(500) NOT NULL DEFAULT '', `share_desc` varchar(200) NOT NULL DEFAULT '', `share_cancel` varchar(200) NOT NULL DEFAULT '', `share_url` varchar(200) NOT NULL DEFAULT '', `share_num` int(10) NOT NULL DEFAULT '0', `follow_url` varchar(200) NOT NULL DEFAULT '', `dateline` int(10) DEFAULT 0, PRIMARY KEY (`id`) ) ENGINE=MyISAM DEFAULT CHARSET=utf8;
